
# Strings Methods

course = "   Python Programming"
print(course.upper())
print(course.lower())
print(course.title())

# Removing white spaces
print(course.rstrip())

# Finding indexes
print(course.find("ram"))

# Replace
print(course.replace("P", "J"))
print(course.replace("mm", "gg"))

print("Pro" in course)
print("Jro" in course)
print("Java" not in course)

